import React from "react";

import { X } from "heroicons-react";

const PrimeLootDropdown = ({ close }) => {
  return (
    <div className="absolute z-10 flex flex-col top-0 shadow-xl right-0 mt-10 bg-gray-800 w-64 overflow-auto">
      <header className="shadow-lg px-2 py-1 inline-flex items-center justify-between">
        <span className="flex-grow">
          <h2 className="text-center">Twitch Prime Loot</h2>
        </span>
        <button onClick={close} className="hover:bg-gray-600 p-2 rounded-lg">
          <X className="h-4 w-4" />
        </button>
      </header>
      <div className="mt-2 p-2 flex flex-col border-b mb-2">
        <h3 className="text-bold text-lg">Claim with Twitch Prime</h3>
        <div className="m-4">
          <TwitchPrimeLogo className="text-purple-700" />
        </div>
        <div className="mb-2">
          <p className="text-sm">
            Support your favorite streamer, plus get monthly games and in-game
            loot, and loads more with Twitch Prime.
          </p>
        </div>
        <button className="text-sm bg-purple-600 px-8 py-2 rounded-lg">
          Start Your Free Trial
        </button>
      </div>
    </div>
  );
};

